package Ejemplos;

public class EJ03_doWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;//declarar una variable
		i=1;//asignar valor a una variable
		//se pueden hacer estas dos operaciones en una sola
		int j=1; //No puedo tener dos variables con el mismo nombre
		
		do {
			System.out.println("El valor de la variable i es: "+i);
			i++;
		} while (i<=10);

	}

}
